        equinix.NetworkACLTemplateInboundRuleArgs(
            dst_port="any",
            protocol="IP",
            src_port="any",
            subnet="1.1.1.1/32",
        ),
        equinix.NetworkACLTemplateInboundRuleArgs(
            dst_port="53,1045,2041",
            protocol="UDP",
            src_port="any",
            subnet="172.16.25.0/24",
        ),
    ])
