    metroCode: "DC",
});
const sv = equinix.GetNetworkAccount({
    metroCode: "SV",
});
const csr1000v_ha = new equinix.NetworkDevice("csr1000v-ha", {
    throughput: 500,
    throughputUnit: "Mbps",
    metroCode: dc.then(dc => dc.metroCode),
    typeCode: "CSR1000V",
    packageCode: "SEC",
    notifications: [
        "john@equinix.com",
        "marry@equinix.com",
        "fred@equinix.com",
    ],
    hostname: "csr1000v-p",
    termLength: 6,
    accountNumber: dc.then(dc => dc.number),
    version: "16.09.05",
    coreCount: 2,
    secondaryDevice: {
        name: "tf-csr1000v-s",
        metroCode: sv.then(sv => sv.metroCode),
        hostname: "csr1000v-s",
        notifications: [
            "john@equinix.com",
            "marry@equinix.com",
        ],
        accountNumber: sv.then(sv => sv.number),
    },
});
